const e=JSON.parse('{"key":"v-4da33ff0","path":"/posts/devlog/20250324.html","title":"Project Miraland 开发日志 [20250324]","lang":"zh-CN","frontmatter":{"article":true,"author":"Neonscape","date":"2025-03-24T00:00:00.000Z","image":"/assets/imgs/202412/202412_5.png","cover":"/assets/imgs/bg_new/202412_6.png","category":["devlog"],"tag":["gamedev"]},"headers":[{"level":2,"title":"角色摄像机的处理逻辑","slug":"角色摄像机的处理逻辑","link":"#角色摄像机的处理逻辑","children":[]}],"git":{"createdTime":1742802299000,"updatedTime":1742802299000,"contributors":[{"name":"Neonscape","email":"neonscapemail@gmail.com","commits":1}]},"readingTime":{"minutes":0.42,"words":126},"filePathRelative":"posts/devlog/20250324.md","localizedDate":"2025年3月24日","excerpt":"<h1> Project Miraland 开发日志 [20250324]</h1>\\n<h2> 角色摄像机的处理逻辑</h2>\\n<p>核心思想：采用越肩视角（右侧）；摄像机悬臂位置应当始终维持角色在屏幕的左侧位置。</p>\\n<p>过程：</p>\\n<ul>\\n<li>使用摄像机的变换计算出标准右向量，并以角色位置为基础计算出摄像机悬臂的目标位置；</li>\\n<li>使用<code>Vinterp</code>将悬臂的位置向目标位置进行插值。</li>\\n</ul>\\n"}');export{e as data};
