const e=JSON.parse('{"key":"v-1297466a","path":"/posts/notes/computer_network_notes.html","title":"2024 春 互联网计算 课程笔记","lang":"zh-CN","frontmatter":{"article":true,"author":"Neonscape","date":"2024-06-04T00:00:00.000Z","image":"/assets/imgs/bg_new/00095-3117391577.png","cover":"/assets/imgs/bg_new/00101-142766746.png","category":["notes"],"tag":["Computer Network"]},"headers":[{"level":2,"title":"第一章 计算机网络与因特网","slug":"第一章-计算机网络与因特网","link":"#第一章-计算机网络与因特网","children":[{"level":3,"title":"1 什么是主机？什么是端系统？","slug":"_1-什么是主机-什么是端系统","link":"#_1-什么是主机-什么是端系统","children":[]},{"level":3,"title":"2 什么是分组？什么是协议？","slug":"_2-什么是分组-什么是协议","link":"#_2-什么是分组-什么是协议","children":[]},{"level":3,"title":"3 什么是客户、服务器？","slug":"_3-什么是客户、服务器","link":"#_3-什么是客户、服务器","children":[]}]}],"git":{"createdTime":1717475663000,"updatedTime":1717475663000,"contributors":[{"name":"Neonscape","email":"neonscapemail@gmail.com","commits":1}]},"readingTime":{"minutes":0.69,"words":206},"filePathRelative":"posts/notes/computer_network_notes.md","localizedDate":"2024年6月4日","excerpt":"<h1> 2024 春 互联网计算 课程笔记</h1>\\n<h2> 第一章 计算机网络与因特网</h2>\\n<h3> 1 什么是主机？什么是端系统？</h3>\\n<p><strong>所有连接到因特网的设备</strong>都称为主机或端系统。</p>\\n<h3> 2 什么是分组？什么是协议？</h3>\\n<p>当一台端系统要向另一台端系统发送数据时，发送端系统将数据分段并为每段数据加上首部字节。这样<strong>包含首部字节的的数据包</strong>称为分组。</p>\\n<p>协议(protocol)定义了在两个或多个通信实体之间交换的报文的格式和顺序，以及报文发送和／或接收一条报文或其他事件所采取的动作。</p>"}');export{e as data};
