import{_ as a}from"./plugin-vue_export-helper-c27b6911.js";import{r as c,o as r,c as t,d as i,a as e,b as o,f as n,e as l}from"./app-84fbeb1d.js";const s="/assets/imgs/bgs/27.png",h={},_=e("h1",{id:"算法笔记-floyd-warshall算法",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#算法笔记-floyd-warshall算法","aria-hidden":"true"},"#"),o(" 算法笔记：Floyd-Warshall算法")],-1),u=e("p",null,[e("s",null,"哦哦，哦哦哦！")],-1),f=e("figure",null,[e("img",{src:s,alt:"好看的",tabindex:"0",loading:"lazy"}),e("figcaption",null,"好看的")],-1),m=e("iframe",{frameborder:"no",border:"0",marginwidth:"0",marginheight:"0",width:"330",height:"86",src:"//music.163.com/outchain/player?type=2&id=1445545567&auto=1&height=66"},null,-1),g=l('<h2 id="算法作用" tabindex="-1"><a class="header-anchor" href="#算法作用" aria-hidden="true">#</a> 算法作用</h2><p>在一张有N个节点的<strong>非负权值</strong>图中，以<code>O(N^3)</code>的复杂度寻找每两个节点之间最短路径的长度的算法。</p><h2 id="准备工作" tabindex="-1"><a class="header-anchor" href="#准备工作" aria-hidden="true">#</a> 准备工作</h2><p>要执行Floyd-Warshall算法，我们需要如下元素：</p><ul><li>一张<strong>不存在负权环的</strong>，有<code>N</code>个节点的图<code>G</code>。</li><li>一个<code>N * N</code>大小的邻接矩阵<code>M</code>，用于表示每两个节点之间的最短距离。</li></ul><h2 id="算法步骤" tabindex="-1"><a class="header-anchor" href="#算法步骤" aria-hidden="true">#</a> 算法步骤</h2><ol start="0"><li>将<code>M</code>中的所有值初始化为+INF.</li><li>枚举<code>G</code>中的所有边<code>&lt;u, v, w&gt;</code>（从<code>u</code>到<code>v</code>的，权值为<code>w</code>的边）；将<code>M[u][v]</code>设为<code>w</code>。</li><li>从<code>1</code>到<code>N</code>枚举中间节点<code>K</code>, 对于每一个<code>K</code>：<br>从<code>1</code>到<code>N</code>枚举源节点<code>S</code>与目标节点<code>D</code>。对于每一个<code>&lt;S, D, K&gt;</code>，有：<br><strong><code>M[S][D] = min(M[S][D], M[S][K] + M[K][D])</code></strong>.</li><li>当该三重循环结束，<code>M[i][j]</code>即为该有向图G中节点<code>i</code>到节点<code>j</code>的最短路径。</li></ol><h2 id="正确性证明" tabindex="-1"><a class="header-anchor" href="#正确性证明" aria-hidden="true">#</a> 正确性证明</h2>',8),p={href:"https://en.wikipedia.org/wiki/Floyd%E2%80%93Warshall_algorithm",target:"_blank",rel:"noopener noreferrer"},b=e("h2",{id:"说明",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#说明","aria-hidden":"true"},"#"),o(" 说明")],-1),x=e("p",null,"如果一张图中存在负权环，那么该图中两点之间的最短路径不存在（可以无限次经过负权环）。",-1);function N(w,y){const d=c("ExternalLinkIcon");return r(),t("div",null,[_,u,i(" more "),f,m,g,e("p",null,[e("a",p,[o("正确性证明（英文）"),n(d)])]),b,x])}const v=a(h,[["render",N],["__file","floyd_warshall_notes.html.vue"]]);export{v as default};
