import{_ as l}from"./plugin-vue_export-helper-c27b6911.js";import{o as a,c as r,d as i,a as e,b as t,e as d}from"./app-baab5c1d.js";const h={},o=e("h1",{id:"移动互联网应用开发-课程笔记",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#移动互联网应用开发-课程笔记","aria-hidden":"true"},"#"),t(" 移动互联网应用开发 课程笔记")],-1),n=d('<h2 id="一-harmonyos的特色、原理和技术" tabindex="-1"><a class="header-anchor" href="#一-harmonyos的特色、原理和技术" aria-hidden="true">#</a> 一 HarmonyOS的特色、原理和技术</h2><h3 id="_1-1-特色" tabindex="-1"><a class="header-anchor" href="#_1-1-特色" aria-hidden="true">#</a> 1.1 特色</h3><ul><li>同一套系统能力，适配多种终端形态；</li><li>支持多设备互联；</li><li>一次开发，多端部署</li></ul><h3 id="_1-2-原理" tabindex="-1"><a class="header-anchor" href="#_1-2-原理" aria-hidden="true">#</a> 1.2 原理</h3><p>系统采用分层结构：内核层、系统服务层、框架层和应用层。</p><ul><li>内核层 <ul><li>分为内核子系统和驱动子系统</li><li>支持多种操作系统</li></ul></li><li>系统服务层 <ul><li>核心能力集合</li><li>系统基础能力子系统</li><li>基础软件服务子系统</li><li>增强软件服务子系统</li><li>硬件服务子系统</li></ul></li><li>框架层 <ul><li>提供了用户语言框架，Ability框架和UI框架。</li></ul></li><li>应用层 <ul><li>包括系统应用和第三方应用。</li></ul></li></ul><h3 id="_1-3-特性" tabindex="-1"><a class="header-anchor" href="#_1-3-特性" aria-hidden="true">#</a> 1.3 特性</h3><ul><li>多设备互联：通过分布式软总线、分布式设备虚拟化、分布式数据管理和分布式任务调度来达到资源共享。</li><li>一次开发，多端部署</li></ul>',8);function s(c,u){return a(),r("div",null,[o,i("more"),i("这课真的不是来打广告的吗..."),n])}const f=l(h,[["render",s],["__file","miad_notes.html.vue"]]);export{f as default};
