const e=JSON.parse('{"key":"v-80bf550e","path":"/posts/misc/modelling_information_and_human_relationships.html","title":"一些想法，考完试整理一下","lang":"zh-CN","frontmatter":{"article":true,"author":"Neonscape","date":"2023-06-12T00:00:00.000Z","image":"/assets/imgs/bgs/7.png","cover":"/assets/imgs/bgs/7.png","category":["misc"],"tag":["model","simulation","TODO","ideas"]},"headers":[{"level":2,"title":"嗯嗯嗯嗯嗯嗯嗯","slug":"嗯嗯嗯嗯嗯嗯嗯","link":"#嗯嗯嗯嗯嗯嗯嗯","children":[]}],"git":{"createdTime":1686589657000,"updatedTime":1686743003000,"contributors":[{"name":"Neonscape","email":"neonscapemail@gmail.com","commits":3}]},"readingTime":{"minutes":2.4,"words":721},"filePathRelative":"posts/misc/modelling_information_and_human_relationships.md","localizedDate":"2023年6月12日","excerpt":"<h1> 一些想法，考完试整理一下</h1>\\n<h2> 嗯嗯嗯嗯嗯嗯嗯</h2>\\n<!--more-->\\n<p>如果把社交网络想象成一张网的话</p>\\n<p>是不是可以量化定义某个东西传播的速度</p>\\n<p>比如一个新表情包 从某一个点进入了一个网络 然后在这个网络上向各个方向以同样的速度扩散</p>\\n<p>每个人是一个节点 节点和节点之间以边相连 然后不同的边对不同的信息有不同的偏好（比如我不会把meme发给辅导员）</p>\\n<p>这就可以抽象成边对信息的偏好</p>\\n<p>以及，每个节点也可能对于信息进行一些更改、增加或者减少</p>\\n<p>什么信息的传播</p>\\n<p>可以包含新闻</p>"}');export{e as data};
