import{_ as o}from"./plugin-vue_export-helper-c27b6911.js";import{o as l,c as r,d as t,a as e,b as i,e as s}from"./app-32d29217.js";const a={},n=e("h1",{id:"第一章-概论",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#第一章-概论","aria-hidden":"true"},"#"),i(" 第一章 概论")],-1),c=s('<h2 id="冯-诺伊曼-计算机模型" tabindex="-1"><a class="header-anchor" href="#冯-诺伊曼-计算机模型" aria-hidden="true">#</a> 冯-诺伊曼 计算机模型</h2><p><strong>冯-诺伊曼</strong> 模型的主要思想如下：</p><ol><li>计算机应该由如下部分组成：</li></ol><ul><li>控制器 <ul><li>自动执行指令</li></ul></li><li>运算器 <ul><li>进行基本的算术运算和逻辑运算等附加运算</li></ul></li><li>存储器 <ul><li>存放数据和指令（两者本质相同）</li></ul></li><li>输入 / 输出设备 <ul><li>与外界进行通信</li></ul></li></ul><ol start="2"><li><p>计算机内部使用 <strong>二进制</strong> 来表示指令和数据。</p><ol><li>指令由操作码（指出指令类型）和地址码（指出操作数的地址）组成。</li><li>程序由一系列指令组成。</li></ol></li><li><p>采用 <strong>存储程序</strong> 工作方式。</p></li></ol><h2 id="计算机系统的性能评价" tabindex="-1"><a class="header-anchor" href="#计算机系统的性能评价" aria-hidden="true">#</a> 计算机系统的性能评价</h2><blockquote><p>我们常使用执行时间来衡量计算机的性能。</p></blockquote><p>我们通常把用户感觉到的响应时间分为两个部分： <strong>用户CPU时间</strong> 和 其他时间（用来运行操作系统、等待I/O，运行其他用户程序的时间等）。</p><p>计算机的 <strong>系统性能</strong> 和 <strong>CPU性能</strong> 并不等价。 <strong>系统性能</strong> 的衡量标准是 <strong>系统的响应时间，影响因素不止有CPU；</strong> <strong>CPU性能</strong> 主要的衡量标准是 <strong>用户CPU时间</strong>。</p>',9);function d(h,g){return l(),r("div",null,[n,t(" more "),c])}const _=o(a,[["render",d],["__file","overview.html.vue"]]);export{_ as default};
